package com.rs.Star;

public class Patterns 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int i;
		int j;
		int n=7;
		for(i=1;i<=n;i++)
		{
			for(j=1;j<=i;j++)
			{
				System.out.print("*");
			}
			System.out.println(" ");
		}

	}//end of main

}//end of class
